getgenv().start = true local plr = game.Players.LocalPlayer function hit(path) local punch = {[1] = "Punch",[2] = nil} game:GetService("ReplicatedStorage"):WaitForChild("RemoteEvent"):FireServer(unpack(punch)) local hit = {[1] = "PunchHit",[2] = {[1] = path}} game:GetService("ReplicatedStorage"):WaitForChild("RemoteEvent"):FireServer(unpack(hit))end while getgenv().start do wait()for i,v in pairs(workspace.Spawns:GetChildren())do if v:FindFirstChild(v.Name)and v[v.Name]:FindFirstChild("Humanoid")and v[v.Name].Humanoid.Health ~= 0 and v[v.Name]:FindFirstChild("HumanoidRootPart")and(plr.Character.HumanoidRootPart.Position - v[v.Name].HumanoidRootPart.Position).Magnitude <= 100 then hit(v[v.Name]) end end end
